.feedback-input {
  color:#eee;
  font-family: Helvetica, Arial, sans-serif;
  font-weight:500;
  font-size: 18px;
  border-radius: 0;
  line-height: 22px;
  background-color: #000000;
  padding: 13px 13px 13px 54px;
  margin-bottom: 10px;
  width:100%;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  -ms-box-sizing: border-box;
  box-sizing: border-box;
  border: 3px solid rgba(0,0,0,0);
}

.feedback-input:focus{
  background: #222;
  box-shadow: 0;
  border: 3px solid #eaf9a4;
  color: #fff;
  outline: none;
  padding: 13px 13px 13px 54px;
}

/* Icons ---------------------------------- */
#name{
  background-image: url(../images/name.svg);
  background-size: 30px 30px;
  background-position: 11px 8px;
  background-repeat: no-repeat;
}

#email{
  background-image: url(../images/email.svg);
  background-size: 30px 30px;
  background-position: 11px 8px;
  background-repeat: no-repeat;
}

#comment{
  background-image: url(../images/comment.svg);
  background-size: 30px 30px;
  background-position: 11px 8px;
  background-repeat: no-repeat;
}

textarea {
  width: 100%;
  height: 150px;
  line-height: 150%;
  resize:vertical;
}

input:hover, textarea:hover,
input:focus, textarea:focus {
  background-color:#222;
}

#button-blue{
  font-family: 'Ubuntu', Arial, Helvetica, sans-serif;
  float:left;
  width: 50%;
  background-color: #272b30;
  color:white;
  font-size:18px;
  padding-top:12px;
  padding-bottom:12px;
  -webkit-transition: all 0.3s;
  -moz-transition: all 0.3s;
  transition: all 0.3s;
  margin-top:-4px;
  font-weight:300;
}

#button-blue:hover{
  background-color: rgba(0,0,0,0);
  color: #eaf9a4;
}





  function checkSendSuccess()
  {
    var success = getURLParameter('success');
    if(success === true)
    {
      alert("Thanks! Your email was successfully sent.");
    }
  }

  function getURLParameter(name) 
  {
    return decodeURIComponent((new RegExp('[?|&]' + name + '=' + '([^&;]+?)(&|#|;|$)').exec(location.search) || [null, ''])[1].replace(/\+/g, '%20')) || null;
  }

  window.onload = checkSendSuccess;

  function validateContactForm() 
  {
    var x = document.forms["contact-form"]["email"].value;
    var atpos = x.indexOf("@");
    var dotpos = x.lastIndexOf(".");
    if (atpos<1 || dotpos<atpos+2 || dotpos+2>=x.length) 
    {
        alert("Please insert a valid e-mail address!!");
        return false;
    }
  }

